legend( h, 'Pr(p,T)', 'Pr vs. p, T', 'Location', 'NorthEast', 'Interpreter', 'none' );
% Label axes
xlabel( 'p', 'Interpreter', 'none' );
ylabel( 'T', 'Interpreter', 'none' );
zlabel( 'Pr', 'Interpreter', 'none' );
grid on
view( -27.1, 45.0 );


