function [fitresult, gof] = fit_c_v(p, T, c_v)
%CREATEFIT(P,T,C_V)
%  Create a fit.
%
%  Data for 'c_v(p,T)' fit:
%      X Input: p
%      Y Input: T
%      Z Output: c_v
%  Output:
%      fitresult : a fit object representing the fit.
%      gof : structure with goodness-of fit info.
%
%  See also FIT, CFIT, SFIT.

%  Auto-generated by MATLAB on 29-Mar-2023 13:06:01


%% Fit: 'c_v(p,T)'.
[xData, yData, zData] = prepareSurfaceData( p, T, c_v );

% Set up fittype and options.
ft = 'linearinterp';
opts = fitoptions( 'Method', 'LinearInterpolant' );
opts.ExtrapolationMethod = 'linear';
opts.Normalize = 'on';

% Fit model to data.
[fitresult, gof] = fit( [xData, yData], zData, ft, opts );

% Plot fit with data.
figure( 'Name', 'c_v(p,T)' );
