function [fitresult, gof] = fit_lambda(p, T, lambda)
%CREATEFIT(P,T,LAMBDA)
%  Create a fit.
%
%  Data for 'lambda(p,T)' fit:
%      X Input: p
%      Y Input: T
%      Z Output: lambda
%  Output:
%      fitresult : a fit object representing the fit.
%      gof : structure with goodness-of fit info.
%
%  See also FIT, CFIT, SFIT.

%  Auto-generated by MATLAB on 29-Mar-2023 13:06:37


%% Fit: 'lambda(p,T)'.
[xData, yData, zData] = prepareSurfaceData( p, T, lambda );

% Set up fittype and options.
ft = 'linearinterp';
opts = fitoptions( 'Method', 'LinearInterpolant' );
opts.ExtrapolationMethod = 'linear';
opts.Normalize = 'on';

% Fit model to data.
[fitresult, gof] = fit( [xData, yData], zData, ft, opts );

% Plot fit with data.
figure( 'Name', 'lambda(p,T)' );
h = plot( fitresult, [xData, yData], zData );
legend( h, 'lambda(p,T)', 'lambda vs. p, T', 'Location', 'NorthEast', 'Interpreter', 'none' );
% Label axes
xlabel( 'p', 'Interpreter', 'none' );
ylabel( 'T', 'Interpreter', 'none' );
zlabel( 'lambda', 'Interpreter', 'none' );
grid on


